mapper.update:
UPDATE product
prod_name = #{prodName}, prod_detail = #{prodDetail:VARCHAR}, manufacture_day = #{manuDate:VARCHAR}, price = #{price:NUMERIC}, image_file = #{fileName:VARCHAR}
WHERE prod_no = #{prodNo}

